<body>
    <div class="container">
        <h1 class="app-title">Task List App</h1>
        <div class="input-area">
            <input type="text" placeholder="Add a new task" id="task-input">
            <button>Add</button>
        </div>
        <div class="task-list"></div>
        <div class="empty-state">No tasks yet. Add one above!</div>
    </div>

    <script src="tasks.js"></script>
    <script>
        const taskInput = document.getElementById('task-input');
        const taskList = document.querySelector('.task-list');
        const emptyState = document.querySelector('.empty-state');

        function addTask(taskText) {
            if (!taskText.trim()) return;

            const taskId = generateUniqueId();
            tasks.push({ id: taskId, text: taskText, completed: false, createdAt: Date.now() });
            saveTasks();
            renderTasks();
            taskInput.value = ''; // Clear input field after successful add
        }

        function toggleComplete(taskId) {
            const task = tasks.find(t => t.id === taskId);
            if (task) {
                task.completed = !task.completed;
                saveTasks();
                renderTasks(); // Re-render tasks after state change
            }
        }

        function deleteTask(taskId) {
            tasks = tasks.filter(t => t.id !== taskId);
            saveTasks();
            renderTasks(); // Re-render tasks after deletion
        }

        function renderTasks() {
            taskList.innerHTML = '';
            tasks.forEach(task => {
                const taskItem = document.createElement('div');
                taskItem.className = 'task-item';
                taskItem.dataset.id = task.id;

                if (task.completed) {
                    taskItem.classList.add('completed');
                }

                const checkbox = document.createElement('div');
                checkbox.className = 'checkbox';
                if (task.completed) {
                    checkbox.classList.add('checked');
                }
                checkbox.addEventListener('click', () => {
                    toggleComplete(task.id);
                });
                taskItem.appendChild(checkbox);

                const taskTextElement = document.createElement('span');
                taskTextElement.className = 'task-text';
                taskTextElement.textContent = escapeHtml(task.text);
                if (task.completed) {
                    taskTextElement.classList.add('completed');
                }
                taskItem.appendChild(taskTextElement);

                const deleteButton = document.createElement('button');
                deleteButton.className = 'delete-btn';
                deleteButton.textContent = 'Delete';
                deleteButton.addEventListener('click', () => {
                    deleteTask(task.id);
                });
                taskItem.appendChild(deleteButton);

                taskList.appendChild(taskItem);
            });

            updateEmptyState();
        }

        function updateEmptyState() {
            if (tasks.length > 0) {
                emptyState.style.display = 'none';
            } else {
                emptyState.style.display = 'block';
            }
        }

        function escapeHtml(text) {
            return text
                .replace(/&/g, "&amp;")
                .replace(/</g, "&lt;")
                .replace(/>/g, "&gt;")
                .replace(/"/g, "&quot;")
                .replace(/'/g, "&#039;");
        }

        document.querySelector('button').addEventListener('click', () => {
            addTask(taskInput.value);
        });

        taskInput.addEventListener('keypress', (e) => {
            if (e.key === 'Enter') {
                addTask(taskInput.value);
            }
        });

        document.addEventListener('DOMContentLoaded', () => {
            loadTasks();
            renderTasks(); // Render tasks on page load
        });
    </script>
</body>
