```taskManager.js
const TASKS_KEY = 'tasks';

function generateUniqueId() {
    return Date.now().toString(36) + Math.random().toString(36).substr(2, 5);
}

function loadTasks() {
    try {
        const tasksJson = localStorage.getItem(TASKS_KEY);
        return tasksJson ? JSON.parse(tasksJson) : [];
    } catch (error) {
        console.error('Error loading tasks from localStorage:', error);
        alert('Failed to load tasks. Please check your browser settings.');
        return [];
    }
}

function saveTasks(tasks) {
    try {
        localStorage.setItem(TASKS_KEY, JSON.stringify(tasks));
    } catch (error) {
        console.error('Error saving tasks to localStorage:', error);
        alert('Failed to save tasks. Please check your browser settings.');
    }
}

let tasks = loadTasks();

function addTask() {
    const input = document.getElementById('task-input');
    const taskList = document.getElementById('task-list');
    const emptyState = document.getElementById('empty-state');

    if (input.value.trim() !== '') {
        const task = {
            id: generateUniqueId(),
            text: input.value,
            completed: false,
            createdAt: new Date().toISOString()
        };

        tasks.push(task);
        saveTasks(tasks);

        renderTask(task);
        input.value = '';
        emptyState.style.display = 'none';
    }
}

function renderTask(task) {
    const taskItem = document.createElement('div');
    taskItem.className = 'task-item';

    const checkbox = document.createElement('input');
    checkbox.type = 'checkbox';
    checkbox.checked = task.completed;
    taskItem.appendChild(checkbox);

    const text = document.createElement('span');
    text.textContent = task.text;
    if (task.completed) {
        text.style.color = '#666666';
        text.style.textDecoration = 'line-through';
        text.style.opacity = '0.7';
    }
    taskItem.appendChild(text);

    const deleteButton = document.createElement('button');
    deleteButton.textContent = 'Delete';
    deleteButton.onclick = () => {
        tasks = tasks.filter(t => t.id !== task.id);
        saveTasks(tasks);
        taskList.removeChild(taskItem);
        if (tasks.length === 0) {
            emptyState.style.display = '';
        }
    };
    taskItem.appendChild(deleteButton);

    const taskList = document.getElementById('task-list');
    taskList.appendChild(taskItem);

    checkbox.addEventListener('change', () => {
        task.completed = checkbox.checked;
        saveTasks(tasks);
        if (checkbox.checked) {
            text.style.color = '#666666';
            text.style.textDecoration = 'line-through';
            text.style.opacity = '0.7';
        } else {
            text.style.color = '#1a1a1a';
            text.style.textDecoration = 'none';
            text.style.opacity = '1';
        }
    });
}

function initTasks() {
    const taskList = document.getElementById('task-list');
    const emptyState = document.getElementById('empty-state');

    tasks.forEach(task => {
        renderTask(task);
    });

    if (tasks.length === 0) {
        emptyState.style.display = '';
    } else {
        emptyState.style.display = 'none';
    }

    // Add event listener for the Add button
    const addButton = document.querySelector('.input-area button');
    addButton.addEventListener('click', addTask);

    // Add keydown event listener to input for Enter key
    const taskInput = document.getElementById('task-input');
    taskInput.addEventListener('keydown', (event) => {
        if (event.key === 'Enter') {
            addTask();
        }
    });
}

window.onload = initTasks;
